module.exports = class ActionRowBuilder {

    constructor() {
        this.row = {
            type: 1
        };
    };

    addComponents(components) {
        if(!this.row.components) this.row.components = [];
        for(const component of components) {
            this.row.components.push(component);
        };

        return this;
    };

    setComponents(components) {
        this.row.components = [];
        for(const component of components) {
            this.row.components.push(component);
        };

        return this;
    };

    toJSON() {
        return this.row;
    };

};