const fetch = require('node-fetch');
const { TypeError, Error } = require('../error.js');

module.exports = async(webhook, avatar) => {

    if(typeof avatar !== 'string') throw new TypeError('INVALID_AVATAR', 'The provided avatar isn\'t an valid String');

    fetch(`https://discord.com/api/v9/webhooks/${(await webhook).id}/${(await webhook).token}`, {
        method: 'PATCH',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({ avatar: avatar }),
    })
    .catch(err => {
        throw new Error('UNEXPECTED_ERROR', err.message);
    });

};